import React, { FC, useState } from "react";
import { Box, Text, Flex, Button } from "@chakra-ui/react";
import { mintAnimalTokenContract } from "../contracts";

interface MainProps {
    //account는 주소
    account: string;
}

const Main: FC<MainProps> = ({ account }) => {
    const [newAnimalCard, setNewAnimalCard] = useState<string>();

    //mint 하는 함수
    const onClickMint = async () => {
        try {
            if (!account) return;

            //mint하는 코드; (컨트랙트 이름).methods.(실행할 함수).(기능) 형식
            const response = await mintAnimalTokenContract.methods
                .mintAnimalToken()
                .send({ from: account });

            //위의 민팅 함수가 정상적으로 실행됐을 경우,
            if(response.status) {
                const balanceLength = await mintAnimalTokenContract.methods
            }

            console.log(response);
        } catch (error) {
            console.error(error);
        }
    };

    return (
        <Flex w="full" h="100vh" justifyContent="center" alignItems="center" direction="column">
            <Box>
                {/*animalcard가 있으면 보여주고 없으면 mint하라고 텍스트*/}
                {newAnimalCard ? (
                    <div>AnimalCard</div>
                ) : (
                    <Text>Let's mint Animal Card!!!</Text>
                )}
            </Box>
            <Button mt={4} size="sm" colorScheme="blue" onClick={onClickMint}>Mint</Button>
        </Flex>
    );
};

export default Main;
